const AIPage: React.FC = () => {
  const navigate = useNavigate();
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const scrollAreaRef = useRef<HTMLDivElement>(null);

  const handleBack = () => {
    navigate(-1);
  };

  const generateMockAIResponse = (userQuestion: string, currentMessages: Message[]): string => {
    const lowerCaseQuestion = userQuestion.toLowerCase().trim();
    const lastAIMessage = currentMessages.length > 0 && currentMessages[currentMessages.length - 1].sender === 'ai' ? currentMessages[currentMessages.length - 1].text.toLowerCase().trim() : '';

    // Specific multi-turn interactions
    if (lastAIMessage.includes("এখন কত সাল চলে? বুঝেছো বাছা।") && lowerCaseQuestion.match(/\d{4}/)) {
      const yearMatch = lowerCaseQuestion.match(/\d{4}/);
      const year = yearMatch ? yearMatch[0] : 'এই সালটা';
      return `ওহ, ${year} সাল! তাহলে আজ ${new Date().toLocaleDateString('bn-BD')}। এবার খুশি? নাকি আপনার বউকে জিজ্ঞাসা করব?`;
    }
    
    if (lastAIMessage.includes("তোমার নাম জানা দরকার।") && lowerCaseQuestion.startsWith("আমার নাম ")) {
      const userNameMatch = lowerCaseQuestion.match(/আমার নাম (.+?)(?: আপনার নাম কি)?$/);
      const userName = userNameMatch && userNameMatch[1] ? userNameMatch[1].trim() : 'রহিম'; // Default to Rahim if not found
      return `তোমার নাম ${userName} দিয়ে কি আমি শরবত বানিয়ে খাবো আর আমার নাম দিয়ে তুমি কি তোমার বউয়ের নাম রাখবে?`;
    }

    // Existing keyword-based funny responses
    for (const [keyword, responses] of funnyResponsesMap.entries()) {
      if (lowerCaseQuestion.includes(keyword.toLowerCase())) {
        const randomIndex = Math.floor(Math.random() * responses.length);
        return responses[randomIndex];
      }
    }

    // Fallback to general funny responses
    const randomIndex = Math.floor(Math.random() * generalFunnyResponses.length);
    return generalFunnyResponses[randomIndex];
  };

  const handleAskQuestion = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim()) return;

    const userMessage: Message = { id: messages.length + 1, sender: 'user', text: question.trim() };
    // Add user message to state immediately
    setMessages(prev => [...prev, userMessage]);
    setQuestion('');
    setIsLoading(true);

    // Simulate AI thinking time
    await new Promise(resolve => setTimeout(resolve, 1500));

    // Pass the updated messages array (including the just-added user message) to generateMockAIResponse
    const aiResponseText = generateMockAIResponse(userMessage.text, [...messages, userMessage]);
    const aiMessage: Message = { id: messages.length + 2, sender: 'ai', text: aiResponseText };
    setMessages(prev => [...prev, aiMessage]);
    setIsLoading(false);
  };

  useEffect(() => {
    if (scrollAreaRef.current) {
      scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="flex flex-col h-full">
      <Card className="w-full flex flex-col flex-1 bg-background/80 backdrop-blur-sm shadow-lg border-primary/20 dark:border-primary/50">
        <CardHeader className="flex flex-row items-center justify-between pb-4 border-b">
          <CardTitle className="text-3xl font-extrabold text-primary dark:text-primary-foreground flex items-center">
            <Button variant="ghost" onClick={handleBack} className="p-0 h-auto mr-2 text-primary dark:text-primary-foreground hover:bg-transparent hover:text-primary/80">
              <ArrowLeft className="h-6 w-6" />
            </Button>
            <Sparkles className="h-7 w-7 mr-2" /> Funny AI
          </CardTitle>
          <CardDescription className="text-muted-foreground hidden sm:block">আপনার যেকোনো প্রশ্ন জিজ্ঞাসা করুন, আর মজার উত্তর পান!</CardDescription>
        </CardHeader>
        <CardContent className="flex-1 p-0 flex flex-col">
          <ScrollArea className="flex-1 w-full p-4" ref={scrollAreaRef}>
            <div className="space-y-4">
              {messages.length === 0 ? (
                <div className="text-center text-muted-foreground p-4 font-bold">কোনো প্রশ্ন জিজ্ঞাসা করা হয়নি। Funny AI-কে কিছু জিজ্ঞাসা করুন!</div>
              ) : (
                messages.map((msg) => (
                  <div
                    key={msg.id}
                    className={cn(
                      "flex items-start gap-3",
                      msg.sender === 'user' ? "justify-end" : "justify-start"
                    )}
                  >
                    {msg.sender === 'ai' && (
                      <Avatar className="h-8 w-8">
                        <AvatarImage src="/public/placeholder.svg" alt="Funny AI" />
                        <AvatarFallback className="bg-primary text-primary-foreground">F.AI</AvatarFallback>
                      </Avatar>
                    )}
                    <div
                      className={cn(
                        "max-w-[70%] p-3 rounded-lg shadow-sm",
                        msg.sender === 'user'
                          ? "bg-primary text-primary-foreground rounded-br-none"
                          : "bg-accent text-accent-foreground rounded-bl-none"
                      )}
                    >
                      <p className="font-semibold">{msg.sender === 'user' ? 'আপনি:' : 'Funny AI:'} {msg.text}</p>
                    </div>
                    {msg.sender === 'user' && (
                      <Avatar className="h-8 w-8">
                        <AvatarImage src="https://github.com/shadcn.png" alt="User" />
                        <AvatarFallback className="bg-secondary text-secondary-foreground">ইউ</AvatarFallback>
                      </Avatar>
                    )}
                  </div>
                ))
              )}
              {isLoading && (
                <div className="flex items-center justify-start gap-3">
                  <Avatar className="h-8 w-8">
                    <AvatarImage src="/public/placeholder.svg" alt="Funny AI" />
                    <AvatarFallback className="bg-primary text-primary-foreground">F.AI</AvatarFallback>
                  </Avatar>
                  <div className="max-w-[70%] p-3 rounded-lg shadow-sm bg-accent text-accent-foreground rounded-bl-none flex items-center">
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                    <span className="font-semibold">Funny AI উত্তর দিচ্ছে...</span>
                  </div>
                </div>
              )}
            </div>
          </ScrollArea>
          <div className="p-4 border-t bg-background/80 backdrop-blur-sm">
            <form onSubmit={handleAskQuestion} className="flex gap-2">
              <Input
                placeholder="আপনার প্রশ্ন জিজ্ঞাসা করুন..."
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                className="flex-1 border-primary/30 focus-visible:ring-primary"
                disabled={isLoading}
              />
              <Button type="submit" disabled={isLoading} className="font-bold">
                {isLoading ? <Loader2 className="h-4 w-4 animate-spin mr-2" /> : <Send className="h-4 w-4 mr-2" />}
                জিজ্ঞাসা করুন
              </Button>
            </form>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default AIPage;